svg.script:
console.log("hello");
var svg = document.getElementsByTagName('svg')[0];
let g1 = document.createElement("g");
g1.className = "headline";
console.log(g1.hasAttribute("class"));
svg.append(g1);
let title = document.createElement("title");
g1.appendChild(title);
title.textContent = "Hello from SVG";
g1.id = "g1";
console.log(svg);

var matches = document.querySelectorAll("g");
console.log(matches);
let element = document.querySelector("title");
console.log(element);

let fragment = document.createDocumentFragment();
let parent = document.createElement("symbol");
let circle1 = document.createElement("circle");
let circle2 = document.createElement("circle");

fragment.appendChild(circle1);
fragment.appendChild(circle2);
svg.append(parent);
parent.appendChild(fragment);
console.log(parent.children);
console.log (parent.firstChild);

let textnode1 = document.createTextNode("hello from text node 1!");
let textnode2 = document.createTextNode("hello from text node 2!");
let textnode3 = document.createTextNode("hello from text node 3!");
let text = document.createElement("title");
text.appendChild(textnode1);
text.appendChild(textnode2);
text.appendChild(textnode3);
svg.append(text);
text.normalize();
console.log(text);

let g2 = document.createElement("g");
svg.append(g2);
g2.className = "classname1 classname2";
g2.classList.remove("classname2");
console.log(g2);

console.log(g2.parentNode);

let nextSib1 = parent.nextSibling;
console.log(nextSib1);
let prevSib1 = g2.previousSibling;
console.log(prevSib1);
if (parent.nextSibling === g2.previousSibling)
  console.log(true);

let childnodes = parent.childNodes;
console.log(childnodes);

if (childnodes[0] === parent.firstElementChild)
  console.log(true);

let nextelsib = g1.nextElementSibling;
console.log('Siblings of g1:');
while (nextelsib) {
  console.log(nextelsib.nodeName);
  nextelsib = nextelsib.nextElementSibling;
}

var g3 = document.createElement("g");
let symb1 = document.createElement("symbol");
g3.appendChild(symb1);
var parentg3 = symb1.parentNode;
let symb2 = document.createElement("symbol");
parentg3.insertBefore(symb2, symb1);
svg.append(g3);
console.log(g3);
